INSERT INTO `jd_class` (`class_id`, `father_id`, `name`, `type`, `channel_id`, `url`, `sort_index`, `is_show`, `is_nav`, `content`, `content_template`, `index_template`) VALUES
(1, 0, '学院概况', 'List', 1, '', 0, 1, 1, '', 2, 1),
(2, 1, '学院简介', 'Index', 1, '', 0, 1, 1, '<h1 style=\"text-align:center;\">\r\n	<span style=\"background-color:#FFFFFF;font-family:宋体;font-size:24px;\">学院简介</span> \r\n</h1>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;font-family:宋体;font-size:24px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;font-family:宋体;font-size:24px;\"> </span> \r\n</p>\r\n<hr />\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-family:宋体;font-size:13px;background-color:#FFFFFF;text-indent:2em;\">\r\n	<span><span style=\"font-size:24px;\"><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-family:宋体;font-size:13px;background-color:#FFFFFF;text-indent:2em;\">\r\n	<span><span style=\"font-size:24px;\">机</span>械与电气工程学院（简称：机电学院）现有自动化、电气工程及其自动化、轨道交通信号与控制、机械设计制造及其自动化等四个专业，其中自动化专业为省级特色专业和校级实施“卓越工程师教育培养计划”专业。目前在校学生<span>1500</span>余人。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-family:宋体;font-size:13px;background-color:#FFFFFF;text-indent:2em;\">\r\n	<span style=\"font-size:small;line-height:26px;\">机电学院下设自动化工程系、电气工程系、机械工程系、轨道交通信号与控制系等4个教学系和</span><span style=\"font-size:small;line-height:26px;\">1</span><span style=\"font-size:small;line-height:26px;\">个实验中心。除电路原理、模拟电子技术、数字电子技术、信号与系统、单片机技术等专业基础实验室外，还拥有</span><span style=\"font-size:small;line-height:26px;\">PLC</span><span style=\"font-size:small;line-height:26px;\">技术、传感器与检测技术、自动控制原理、现场总线技术、运动控制技术等专业实验室。机电学院建设了电工实训室、过程控制实训室、</span><span style=\"font-size:small;line-height:26px;\">PLC</span><span style=\"font-size:small;line-height:26px;\">运用实训室、工厂供配电实训室、电力系统自动化实训室、数控加工实训中心等。这些实验实训室的建设，既保障了教学科研的需要，又强化了学生专业能力的培养与训练。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-family:宋体;font-size:13px;background-color:#FFFFFF;text-indent:2em;\">\r\n	<span style=\"font-size:small;line-height:26px;\">机电学院拥有一支高学历、高素质、年轻化、爱岗敬业、积极进取、勇于创新的教师队伍。其中教授</span><span style=\"font-size:small;line-height:26px;\">2</span><span style=\"font-size:small;line-height:26px;\">名、副教授5</span><span style=\"font-size:small;line-height:26px;\">名、在读博士学位</span><span style=\"font-size:small;line-height:26px;\">2</span><span style=\"font-size:small;line-height:26px;\">名。教师队伍中</span><span style=\"font-size:small;line-height:26px;\">1</span><span style=\"font-size:small;line-height:26px;\">人获安徽省高等学校十五优秀人才计划优秀中青年骨干教师、</span><span style=\"font-size:small;line-height:26px;\">1</span><span style=\"font-size:small;line-height:26px;\">人入选淮南市舜耕英才培养对象、</span><span style=\"font-size:small;line-height:26px;\">2</span><span style=\"font-size:small;line-height:26px;\">人获省级教坛新秀荣誉称号、</span><span style=\"font-size:small;line-height:26px;\">1</span><span style=\"font-size:small;line-height:26px;\">人获校级教学名师荣誉称号。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-family:宋体;font-size:13px;background-color:#FFFFFF;text-indent:2em;\">\r\n	<span style=\"font-size:small;line-height:26px;\">机电学院教师重视教学研究和科研工作，承担了多项省级、市级、校级教科研项目，在《中国科学》、《电子学报》、《控制与决策》、《信息与控制》、《中国科学技术大学学报》等学术刊物和国际学术会议上发表论文</span><span style=\"font-size:small;line-height:26px;\">70</span><span style=\"font-size:small;line-height:26px;\">余篇，其中</span><span style=\"font-size:small;line-height:26px;\">SCI/EI</span><span style=\"font-size:small;line-height:26px;\">收录论文</span><span style=\"font-size:small;line-height:26px;\">20</span><span style=\"font-size:small;line-height:26px;\">余篇竞赛。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-family:宋体;font-size:13px;background-color:#FFFFFF;text-indent:2em;\">\r\n	<span style=\"font-size:small;line-height:26px;\">机电学院按照学校“地方性、应用型”的办学定位和社会经济发展对专业人才的需求，不断探索课程体系和人才培养模式的改革与创新，注重面向工程应用，强化专业能力培养，尤其重视对学生实践能力和创新精神的培养，在引企入校专业实习和第二课堂实践创新训练方面形成特色。学生积极参加各类科技创新创业活动，多次在国家级、省级专业竞赛和创业计划大赛中获奖。其中“搜科网络技术股份公司”获第五届“挑战杯”安徽省大学生创业计划竞赛金奖和第八届“挑战杯”全国大学生创业计划大赛银奖、学生参加第九届全国大学生“飞思卡尔”杯智能汽车竞赛全国总决赛获二等奖，在首届“西门子”杯工业自动化竞赛中荣获国家二等奖。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-family:宋体;font-size:13px;background-color:#FFFFFF;text-indent:2em;\">\r\n	<span style=\"font-size:small;line-height:26px;\">近年来，机电学院每年都会有10多</span><span style=\"font-size:small;line-height:26px;\">名学生考取了浙江大学、四川大学、合肥工业大学、安徽大学、重庆邮电大学、南京邮电大学、华东交通大学、浙江工业大学、杭州电子科技大学等知名高校的研究生。机电学院毕业生基础扎实、适应力强、具备较强的专业技能和创新精神，毕业生普遍受到用人单位的欢迎，成为企业“留得住、用得上、干得好、有潜力”工程技术人才，就业率一直保持在很高的水平。</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-family:宋体;font-size:13px;background-color:#FFFFFF;text-indent:2em;\">\r\n	<span style=\"font-size:small;line-height:26px;\">学校第二次党代会提出了“深化改革、转型发展、提升内涵，为建设地方应用型高水平大学而努力奋斗”的发展目标，随着学校应用型本科办学转型，机械与电气工程学院将党的十八大和十八届三中四中会会精神为指引，以更快的发展步伐、更新的精神状态来迎接更辉煌的明天！</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', 2, 3),
(3, 1, '机构设置', 'Index', 1, '', 0, 1, 1, '<p>\r\n	<br />\r\n</p>\r\n<h1 style=\"text-align:center;\">\r\n	学院机构设置\r\n</h1>\r\n<p>\r\n	<br />\r\n</p>\r\n<table width=\"100%\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\" style=\"color:#000000;font-family:宋体;font-size:13px;background-color:#FFFFFF;\">\r\n	<tbody>\r\n		<tr>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"19%\">\r\n				<p>\r\n					<br class=\"Apple-interchange-newline\" />\r\n机构性质\r\n				</p>\r\n			</td>\r\n			<td width=\"36%\">\r\n				<br />\r\n				<p>\r\n					职务\r\n				</p>\r\n			</td>\r\n			<td width=\"18%\">\r\n				<br />\r\n				<p>\r\n					姓名\r\n				</p>\r\n			</td>\r\n			<td width=\"25%\">\r\n				<br />\r\n				<p>\r\n					办公电话\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"19%\" rowspan=\"4\">\r\n				<br />\r\n				<p>\r\n					学院领导\r\n				</p>\r\n			</td>\r\n			<td width=\"36%\">\r\n				<br />\r\n				<p>\r\n					院 长\r\n				</p>\r\n			</td>\r\n			<td width=\"18%\">\r\n				<br />\r\n				<p>\r\n					赵耀军\r\n				</p>\r\n			</td>\r\n			<td width=\"25%\">\r\n				<br />\r\n				<p>\r\n					0554-6673717\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"36%\">\r\n				<br />\r\n				<p>\r\n					党总支书记\r\n				</p>\r\n			</td>\r\n			<td width=\"18%\">\r\n				<br />\r\n				<p>\r\n					王战修\r\n				</p>\r\n			</td>\r\n			<td width=\"25%\">\r\n				<br />\r\n				<p>\r\n					0554-6863007\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"36%\">\r\n				<br />\r\n				<p>\r\n					常务副院长\r\n				</p>\r\n			</td>\r\n			<td width=\"18%\">\r\n				<br />\r\n				<p>\r\n					杨国诗\r\n				</p>\r\n			</td>\r\n			<td width=\"25%\">\r\n				<br />\r\n				<p>\r\n					0554-6672520\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"36%\">\r\n				<br />\r\n				<p>\r\n					党总支副书记\r\n				</p>\r\n			</td>\r\n			<td width=\"18%\">\r\n				<br />\r\n				<p>\r\n					唐 亮\r\n				</p>\r\n			</td>\r\n			<td width=\"25%\">\r\n				<br />\r\n				<p>\r\n					0554-6672521\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr